// Helper function to get a specific verse
window.getSurah3Verse = function(verseNumber) {
    if (verseNumber >= 1 && verseNumber <= 200) {
        return surah3Complete.verses_data[verseNumber - 1];
    }
    return null;
};

// Helper function to get verses range
window.getSurah3Verses = function(startVerse, endVerse) {
    if (startVerse < 1 || endVerse > 200 || startVerse > endVerse) {
        return [];
    }
    return surah3Complete.verses_data.slice(startVerse - 1, endVerse);
};

